body.script:
//
var d = new Date();
var y = d.getYear() - 100;
var m = d.getMonth() + 1;
var datenum = [y, m, d.getDate(), d.getHours(), d.getMinutes()];
var i;
for(i = 0; i < datenum.length; i++){
datenum[i] += "";
while(datenum[i].length < 2) datenum[i] = "0" + datenum[i];}
var p = document.getElementsByTagName("p")
p[0].innerHTML = datenum.join("");
p[1].innerHTML = datenum[1].length;
//
